nbData = 449;
for i=1:nbData
    data(i) = (data(i)*5)/1024;
end
%% Déclaration des variables
data_excel = transpose(data);
fs = 500;
time = 0:1/fs:(nbData-1)/fs;
PartSize = 70;
partStep = 7;
modIndex = mod(nbData,PartSize+partStep);
amplifier = 50;
dataPart = zeros(1,PartSize);
dataRest = zeros(1,modIndex);
filtData = zeros(1,nbData);

FH = 65;
FL = 180;
WL = 1501;
%% Filtrage des premières données
% for i=1:PartSize-partStep
%     dataPart(i) = 0;
% end
% for i=PartSize-partStep+1:PartSize
%     dataPart(i) = data(i);
% end
for i=1:PartSize
    dataPart(i) = data(i);
end
smoothPart = FilterEMGData(dataPart,fs,FH,FL,WL);
for i=1:PartSize
    filtData(i) = smoothPart(i);
end
%% Algorithme de filtrage des données
for i = PartSize+partStep:partStep:nbData
    for j=1:PartSize
        dataPart(j) = data(i-(PartSize-j));
    end
    smoothPart = FilterEMGData(dataPart,fs,FH,FL,WL);
    for M = 1:PartSize
        if(filtData(i-(PartSize-M)) <= 0)
            filtData(i-(PartSize-M)) = smoothPart(M);
        end
    end
end
%% Gestion des données pas modifiées par l'algorithme
% for i=1:modIndex
%     dataRest(i) = data(nbData-(modIndex-i));
% end
% smoothRest = FilterEMGData(dataRest,fs,FH,FL,WL);
% for i=1:modIndex
%     filtData(nbData-(modIndex-i)) = filtData(nbData-(modIndex-i))+smoothRest(i);
% end
%% Amplification
smoothExcel = transpose(filtData);
for i=1:nbData
    filtData(i) = filtData(i)*amplifier;
end
